const NorthAmericaMap: React.FC<NorthAmericaMapProps> = ({ selectedCountryId }) => {
    const handleCountryClick = (event: MouseEvent<SVGPathElement>) => {
        const countryId = event.currentTarget.id;
        if (countryId) {
            console.log(`Clicked on country: ${countryId}`);
        }
    };

    return (
        <div>
            <svg xmlns="http://www.w3.org/2000/svg"
                version="1.2" id="svg2"
                style={{ strokeLinejoin: 'round' }}
                className='stroke-[#000] fill-[#f2f2f2]'
                enableBackground="new 0 0 1162 1001"
                viewBox="0 0 1162 1001"
                width="1162" height="1001"
                >

                {northAmericaPaths.map((country) => (
                    <path className='cursor-pointer' onClick={handleCountryClick}
                        key={country.id}
                        id={country.id}
                        data-name={country.dataName}
                        d={country.d}
                        style={
                            selectedCountryId === country.id
                            ? { fill: 'green' }
                            : { transition: 'transform 0.2s' }
                        }
                    />
                ))}

            </svg>
        </div>
    );
}

export default NorthAmericaMap;
